const WePayAPI = require('./WePay');


const rechargeCryptoBalance = {

   "vendorKey": "5f5bdaed-f82b-4b82-b3f5-1d562633da5b",

   "txId": "2bf779e2a311c2629df977b0bb105879411fd71f5839972c4ed1d3278f80170f",

   "timestamp": new Date("2018-09-10T23:11:03-03:00").getTime(),

   "blockchain": "LTC",

   "currency": "BRL",

   "coinAmount": "0.04494037",

   "currencyAmount": "10.00",

   "buyer": "43de9565-943e-49ff-b808-82d54a87199f"

};


WePayAPI.rechargeCryptoBalance(rechargeCryptoBalance, function (result) {

  console.log("result: ", result);

});

$WePayAPI = new WePayAPI(Environment::sandbox());


$rechargeCryptoBalance = new RechargeCryptoBalance();


$rechargeCryptoBalance->setAmount(10.00);

$rechargeCryptoBalance->setBlockchain(BlockChainType::LTC);

$rechargeCryptoBalance->setCurrency(CurrencyType::BRL);

$rechargeCryptoBalance->setPan("9999999999999999");

$rechargeCryptoBalance->setPassword("1234");

$rechargeCryptoBalance->setVendorKey("5f5bdaed-f82b-4b82-b3f5-1d562633da5b");


$rechargeCryptoBalanceResponse = $WePayAPI->rechargeCryptoBalance($rechargeCryptoBalance);


print_r($rechargeCryptoBalanceResponse);

WePayAPI WePayAPI = new WePayAPI(EnviromentType.SandBox);


RechargeCryptoBalance rechargeCryptoBalance = new RechargeCryptoBalance();

rechargeCryptoBalance.pan = "9999999999999999";

rechargeCryptoBalance.password = "1234";

rechargeCryptoBalance.blockchain = BlockChainType.LTC;

rechargeCryptoBalance.amount = 13.88;

rechargeCryptoBalance.currency = CurrencyType.BRL;

rechargeCryptoBalance.vendorKey = "5f5bdaed-f82b-4b82-b3f5-1d562633da5b";


var rechargeCryptoBalanceReturn = WePayAPI.RechargeCryptoBalance(rechargeCryptoBalance);


Console.WriteLine("Recharge of Crypto Balance: " + JsonConvert.SerializeObject(rechargeCryptoBalanceReturn))

const WePayAPI = require('./WePay');


const payment = {

  "pan": "9999999999999999",

  "password": "1234",

  "blockchain": "LTC",

  "amount": 10.03,

  "currency": "BRL",

  "vendorKey": "5f5bdaed-f82b-4b82-b3f5-1d562633da5b"

};


WePayAPI.makePayment(payment, function (result) {

  console.log("result: ", result);

});

$WePayAPI = new WePayAPI(Environment::sandbox());


$makePayment = new MakePayment();


$makePayment->setAmount(10.00);

$makePayment->setBlockchain(BlockChainType::LTC);

$makePayment->setCurrency(CurrencyType::BRL);

$makePayment->setPan("9999999999999999");

$makePayment->setPassword("1234");

$makePayment->setVendorKey("5f5bdaed-f82b-4b82-b3f5-1d562633da5b");


$makePaymentResponse = $WePayAPI->makePayment($makePayment);


print_r($makePaymentResponse);

WePayAPI WePayAPI = new WePayAPI(EnviromentType.SandBox);


MakePayment makePayment = new MakePayment();

makePayment.pan = "9999999999999999";

makePayment.password = "1234";

makePayment.blockchain = BlockChainType.LTC;

makePayment.amount = 13.88;

makePayment.currency = CurrencyType.BRL;

makePayment.vendorKey = "5f5bdaed-f82b-4b82-b3f5-1d562633da5b";


var makePaymentReturn = WePayAPI.MakePayment(makePayment);


Console.WriteLine("Make a Payment: " + JsonConvert.SerializeObject(makePaymentReturn));

const WePayAPI = require('./WePay');


const paymentRecurring = {

  "pan": "9999999999999999",

  "password": "1234",

  "blockchain": "LTC",

  "amount": 10.03,

  "currency": "BRL",

  "period": "W",

  "frequency": "10",

  "execute": false,

  "vendorKey": "5f5bdaed-f82b-4b82-b3f5-1d562633da5b"

};


WePayAPI.makeRecurringPayment(paymentRecurring, function (result) {

  console.log("result: ", result);

});

$WePayAPI = new WePayAPI(Environment::sandbox());


$makeRecurringPayment = new MakeRecurringPayment();


$makeRecurringPayment->setAmount(10.00);

$makeRecurringPayment->setBlockchain(BlockChainType::LTC);

$makeRecurringPayment->setCurrency(CurrencyType::BRL);

$makeRecurringPayment->setPeriod(PeriodType::M);

$makeRecurringPayment->setFrequency(12);

$makeRecurringPayment->setExecute(true);

$makeRecurringPayment->setPan("9999999999999999");

$makeRecurringPayment->setPassword("1234");

$makeRecurringPayment->setVendorKey("5f5bdaed-f82b-4b82-b3f5-1d562633da5b");


$makeRecurringPaymentResponse = $WePayAPI->makeRecurringPayment($makePayment);


print_r($makeRecurringPaymentResponse);

WePayAPI WePayAPI = new WePayAPI(EnviromentType.SandBox);


MakeRecurringPayment makeRecurringPayment = new MakeRecurringPayment();

makeRecurringPayment.pan = "9999999999999999";

makeRecurringPayment.password = "1234";

makeRecurringPayment.blockchain = BlockChainType.LTC;

makeRecurringPayment.amount = 13.88;

makeRecurringPayment.currency = CurrencyType.BRL;

makeRecurringPayment.period = PeriodType.M;

makeRecurringPayment.vendorKey = "5f5bdaed-f82b-4b82-b3f5-1d562633da5b";


var makeRecurringPaymentReturn = WePayAPI.MakeRecurringPayment(makeRecurringPayment);


Console.WriteLine("Make a Recurring Payment: " + JsonConvert.SerializeObject(makeRecurringPaymentReturn));

