$WePayAPI = new WePayAPI(Environment::sandbox());


$cancelPayment = new CancelPayment();


$cancelPayment->setTxId("2bf779e2a311c2629df977b0bb105879411fd71f5839972c4ed1d3278f80170f");

$cancelPayment->setPassword("1234");

$cancelPayment->setVendorKey("5f5bdaed-f82b-4b82-b3f5-1d562633da5b");


$cancelPaymentResponse = $WePayAPI->cancelPayment($cancelPayment);

print_r($cancelPaymentResponse);

WePayAPI WePayAPI = new WePayAPI(WePayAPI.Environment.SANDBOX);


WePayAPI.start(new CancelPayment.Builder()

.withPassWord("1234")

.withVendorKey("5f5bdaed-f82b-4b82-b3f5-1d562633da5b")

.withTxId("2bf779e2a311c2629df977b0bb105879411fd71f5839972c4ed1d3278f80170f")

.build());

WePayAPI WePayAPI = new WePayAPI(EnviromentType.SandBox);


